mapper.select:
select

from t_job


    and id like CONCAT('%', #{id},'%')


    and company_id like CONCAT('%', #{companyId},'%')


    and creator_id like CONCAT('%', #{creatorId},'%')


    and job_type = #{jobType,jdbcType=TINYINT}


    and name like CONCAT('%', #{name},'%')


    and category_id = #{categoryId,jdbcType=INTEGER}


    and city_id = #{cityId,jdbcType=INTEGER}


    and status = #{status,jdbcType=TINYINT}


    and (pub_time between #{startPubTime} and  #{endPubTime})

  and del_flag = 0

select t_dict.id as recruitType, count(t_job.recruit_type) as count from t_dict left join t_job on t_dict.id = t_job.recruit_type and t_job.company_id = #{companyId,jdbcType=BIGINT}
and t_dict.type = 12
group by t_job.recruit_type,t_dict.id order by t_dict.value asc
