mapper.select:
select
from t_job as j inner join t_company as c
and j.id like CONCAT('%', #{id},'%')
and j.company_id like CONCAT('%', #{companyId},'%')
and j.creator_id like CONCAT('%', #{creatorId},'%')
and j.job_type = #{jobType,jdbcType=TINYINT}
and j.name like CONCAT('%', #{name},'%')
and j.category_id = #{categoryId,jdbcType=INTEGER}
and j.city_id = #{cityId,jdbcType=INTEGER}
and j.status = #{status,jdbcType=TINYINT}
and (j.pub_time between #{startPubTime} and #{endPubTime})
and c.name != '' and j.company_id = c.id and j.del_flag = 0
